import React, { useState, useEffect } from 'react';
import { Doughnut, Bar } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement } from 'chart.js';
import axios from 'axios';
import { Box, Typography, Select, MenuItem, FormControl, InputLabel } from '@mui/material';
import TransactionForm from '../components/transaction/TransactionForm';
import TransactionList from '../components/transaction/TransactionList';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

const FinanceDashboard = () => {
  const [timeRange, setTimeRange] = useState('Last 7 days');
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch transactions from API
  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const res = await axios.get('/api/transactions');
        setTransactions(res.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching transactions', err);
        setLoading(false);
      }
    };
    fetchTransactions();
  }, []);

  // Calculate financial metrics
  const incomeTotal = transactions
    .filter(tx => tx.type === 'income')
    .reduce((sum, tx) => sum + tx.amount, 0);

  const expenseTotal = transactions
    .filter(tx => tx.type === 'expense')
    .reduce((sum, tx) => sum + tx.amount, 0);

  const currentBalance = incomeTotal - expenseTotal;

  // Handle new transaction
  const handleTransactionAdded = (newTransaction) => {
    setTransactions(prev => [newTransaction, ...prev]);
  };

  // Handle transaction deletion
  const handleDeleteTransaction = async (id) => {
    try {
      await axios.delete(`/api/transactions/${id}`);
      setTransactions(prev => prev.filter(tx => tx._id !== id));
    } catch (err) {
      console.error('Error deleting transaction', err);
    }
  };

  // Prepare chart data
  const getChartData = () => {
    const categories = {};
    transactions
      .filter(tx => tx.type === 'expense')
      .forEach(tx => {
        categories[tx.category] = (categories[tx.category] || 0) + tx.amount;
      });
    
    return {
      labels: Object.keys(categories),
      amounts: Object.values(categories),
      colors: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF']
    };
  };

  const chartData = getChartData();

  return (
    <Box sx={{ p: 3 }}>
      {/* Dashboard Header */}
      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        mb: 4 
      }}>
        <Typography variant="h4">Finance Dashboard</Typography>
        <FormControl sx={{ minWidth: 180 }}>
          <InputLabel>Time Range</InputLabel>
          <Select
            value={timeRange}
            onChange={(e) => setTimeRange(e.target.value)}
            label="Time Range"
          >
            <MenuItem value="Last 7 days">Last 7 days</MenuItem>
            <MenuItem value="Last 30 days">Last 30 days</MenuItem>
            <MenuItem value="Last quarter">Last quarter</MenuItem>
          </Select>
        </FormControl>
      </Box>

      {/* Transaction Form */}
      <TransactionForm onTransactionAdded={handleTransactionAdded} />

      {/* Financial Overview Cards */}
      <Box sx={{ 
        display: 'grid', 
        gridTemplateColumns: { md: 'repeat(3, 1fr)' }, 
        gap: 3, 
        mt: 3 
      }}>
        {/* Balance Card */}
        <Box sx={{ 
          p: 3, 
          border: '1px solid #eee', 
          borderRadius: 2 
        }}>
          <Typography variant="h6" gutterBottom>Current Balance</Typography>
          <Typography variant="h4" sx={{ 
            color: currentBalance >= 0 ? 'success.main' : 'error.main' 
          }}>
            ${currentBalance.toLocaleString()}
          </Typography>
          <Box sx={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            mt: 2 
          }}>
            <Box>
              <Typography variant="subtitle2">Income</Typography>
              <Typography color="success.main">
                +${incomeTotal.toLocaleString()}
              </Typography>
            </Box>
            <Box>
              <Typography variant="subtitle2">Expenses</Typography>
              <Typography color="error.main">
                -${expenseTotal.toLocaleString()}
              </Typography>
            </Box>
          </Box>
        </Box>

        {/* Spending Categories Card */}
        <Box sx={{ 
          p: 3, 
          border: '1px solid #eee', 
          borderRadius: 2 
        }}>
          <Typography variant="h6" gutterBottom>Spending Breakdown</Typography>
          <Box sx={{ height: 200 }}>
            <Doughnut
              data={{
                labels: chartData.labels,
                datasets: [{
                  data: chartData.amounts,
                  backgroundColor: chartData.colors
                }]
              }}
              options={{
                maintainAspectRatio: false
              }}
            />
          </Box>
        </Box>

        {/* Cash Flow Card */}
        <Box sx={{ 
          p: 3, 
          border: '1px solid #eee', 
          borderRadius: 2 
        }}>
          <Typography variant="h6" gutterBottom>Monthly Cash Flow</Typography>
          <Box sx={{ height: 200 }}>
            <Bar
              data={{
                labels: ['Week 1', 'Week 2', 'Week 3', 'Week 4'],
                datasets: [
                  {
                    label: 'Income',
                    data: [3500, 2800, 4200, 3900],
                    backgroundColor: '#4CC9F0'
                  },
                  {
                    label: 'Expenses',
                    data: [2200, 1800, 2500, 2100],
                    backgroundColor: '#F72585'
                  }
                ]
              }}
              options={{
                maintainAspectRatio: false,
                responsive: true,
                scales: {
                  x: { stacked: true },
                  y: { stacked: true }
                }
              }}
            />
          </Box>
        </Box>
      </Box>

      {/* Transactions List */}
      <Box sx={{ mt: 4 }}>
        <Typography variant="h5" gutterBottom>Recent Transactions</Typography>
        <TransactionList 
          transactions={transactions} 
          onDelete={handleDeleteTransaction}
          loading={loading}
        />
      </Box>
    </Box>
  );
};

export default FinanceDashboard;
