import React, { useState, useEffect } from 'react';
import { Doughnut, Bar } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement } from 'chart.js';
import axios from 'axios';
import TransactionForm from './TransactionForm';
import TransactionList from './TransactionList';
import { Box, Typography, Select, MenuItem, FormControl, InputLabel } from '@mui/material';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

const FinanceDashboard = () => {
  const [timeRange, setTimeRange] = useState('Last 7 days');
  const [selectedFilters, setSelectedFilters] = useState(['Income', 'Expenses', 'Investments']);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch transactions from API
  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const res = await axios.get('/api/transactions');
        setTransactions(res.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching transactions', err);
        setLoading(false);
      }
    };
    fetchTransactions();
  }, []);

  // Calculate derived data
  const incomeTotal = transactions
    .filter(tx => tx.type === 'income')
    .reduce((sum, tx) => sum + tx.amount, 0);

  const expenseTotal = transactions
    .filter(tx => tx.type === 'expense')
    .reduce((sum, tx) => sum + tx.amount, 0);

  const currentBalance = incomeTotal - expenseTotal;

  // Handle new transaction
  const handleTransactionAdded = (newTransaction) => {
    setTransactions(prev => [newTransaction, ...prev]);
  };

  // Delete transaction
  const handleDeleteTransaction = async (id) => {
    try {
      await axios.delete(`/api/transactions/${id}`);
      setTransactions(prev => prev.filter(tx => tx._id !== id));
    } catch (err) {
      console.error('Error deleting transaction', err);
    }
  };

  // Get expense categories data for chart
  const getExpenseCategoriesData = () => {
    const categories = {};
    transactions
      .filter(tx => tx.type === 'expense')
      .forEach(tx => {
        categories[tx.category] = (categories[tx.category] || 0) + tx.amount;
      });
    
    return {
      labels: Object.keys(categories),
      amounts: Object.values(categories)
    };
  };

  const expenseData = getExpenseCategoriesData();

  return (
    <Box sx={{ p: 3 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 4 }}>
        <Typography variant="h4">MY Finance</Typography>
        <FormControl sx={{ minWidth: 180 }}>
          <InputLabel>Time Range</InputLabel>
          <Select
            value={timeRange}
            onChange={(e) => setTimeRange(e.target.value)}
            label="Time Range"
          >
            <MenuItem value="Last 7 days">Last 7 days</MenuItem>
            <MenuItem value="Last 30 days">Last 30 days</MenuItem>
            <MenuItem value="Last quarter">Last quarter</MenuItem>
            <MenuItem value="Custom range">Custom range</MenuItem>
          </Select>
        </FormControl>
      </Box>

      {/* Transaction Form */}
      <TransactionForm onTransactionAdded={handleTransactionAdded} />

      <Box sx={{ display: 'grid', gridTemplateColumns: { md: 'repeat(3, 1fr)' }, gap: 3, mt: 3 }}>
        {/* Balance Overview */}
        <Box sx={{ p: 3, border: '1px solid #eee', borderRadius: 2 }}>
          <Typography variant="h6" gutterBottom>Balance Overview</Typography>
          <Typography variant="h4" sx={{ color: currentBalance >= 0 ? 'success.main' : 'error.main' }}>
            ${currentBalance.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
          </Typography>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 2 }}>
            <Box>
              <Typography variant="subtitle2">Income</Typography>
              <Typography color="success.main">
                +${incomeTotal.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
              </Typography>
            </Box>
            <Box>
              <Typography variant="subtitle2">Expenses</Typography>
              <Typography color="error.main">
                -${expenseTotal.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
              </Typography>
            </Box>
          </Box>
        </Box>

        {/* Spending Categories */}
        <Box sx={{ p: 3, border: '1px solid #eee', borderRadius: 2 }}>
          <Typography variant="h6" gutterBottom>Spending Categories</Typography>
          <Box sx={{ height: 200 }}>
            <Doughnut
              data={{
                labels: expenseData.labels,
                datasets: [{
                  data: expenseData.amounts,
                  backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF']
                }]
              }}
            />
          </Box>
        </Box>

        {/* Cash Flow */}
        <Box sx={{ p: 3, border: '1px solid #eee', borderRadius: 2 }}>
          <Typography variant="h6" gutterBottom>Cash Flow</Typography>
          <Box sx={{ height: 200 }}>
            <Bar
              data={{
                labels: ['Week 1', 'Week 2', 'Week 3', 'Week 4'],
                datasets: [
                  {
                    label: 'Income',
                    data: [3500, 2800, 4200, 3900],
                    backgroundColor: '#4CC9F0'
                  },
                  {
                    label: 'Expenses',
                    data: [2200, 1800, 2500, 2100],
                    backgroundColor: '#F72585'
                  }
                ]
              }}
              options={{
                responsive: true,
                scales: {
                  x: { stacked: true },
                  y: { stacked: true }
                }
              }}
            />
          </Box>
        </Box>
      </Box>

      {/* Transactions List */}
      <Box sx={{ mt: 4 }}>
        <TransactionList 
          transactions={transactions} 
          onDelete={handleDeleteTransaction}
        />
      </Box>
    </Box>
  );
};

export default FinanceDashboard;
