function BalanceCard({ balance }) {
  return (
    <div className="card balance-card">
      <h3>Current Balance</h3>
      <p className={balance >= 0 ? 'positive' : 'negative'}>
        ${balance.toFixed(2)}
      </p>
    </div>
  );
}

export default BalanceCard;
