import React, { useState, useEffect, useContext } from 'react';
import BalanceCard from '../components/dashboard/BalanceCard';
import RecentTransactions from '../components/dashboard/RecentTransactions';
import BudgetCard from '../components/dashboard/BudgetCard';
import SpendingChart from '../components/analytics/SpendingChart';
import MonthlyTrends from '../components/analytics/MonthlyTrends';
import axios from 'axios';
import AuthContext from '../context/AuthContext';

const Dashboard = () => {
  const [transactions, setTransactions] = useState([]);
  const [balance, setBalance] = useState(0);
  const [loading, setLoading] = useState(true);
  const { user } = useContext(AuthContext);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const config = {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        };
        
        // Fetch transactions
        const res = await axios.get('/api/transactions', config);
        setTransactions(res.data);
        
        // Calculate balance
        const calculatedBalance = res.data.reduce((acc, transaction) => {
          return transaction.type === 'income' 
            ? acc + transaction.amount 
            : acc - transaction.amount;
        }, 0);
        
        setBalance(calculatedBalance);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching data:', err);
        setLoading(false);
      }
    };

    if (user) {
      fetchData();
    }
  }, [user]);

  if (loading) {
    return (
      <div className="dashboard">
        <h1>Loading your financial data...</h1>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>Welcome back, {user?.username}!</h1>
        <p className="dashboard-subtitle">Here's your financial overview</p>
      </div>
      
      <div className="dashboard-grid">
        <BalanceCard balance={balance} />
        <BudgetCard />
        
        <div className="card quick-actions">
          <h3>Quick Actions</h3>
          <div className="actions-grid">
            <button className="action-button">
              <i className="fas fa-plus-circle"></i>
              <span>Add Income</span>
            </button>
            <button className="action-button">
              <i className="fas fa-minus-circle"></i>
              <span>Add Expense</span>
            </button>
            <button className="action-button">
              <i className="fas fa-chart-line"></i>
              <span>View Reports</span>
            </button>
            <button className="action-button">
              <i className="fas fa-cog"></i>
              <span>Settings</span>
            </button>
          </div>
        </div>
        
        <RecentTransactions transactions={transactions} />
        <SpendingChart transactions={transactions} />
        <MonthlyTrends transactions={transactions} />
      </div>
    </div>
  );
};

export default Dashboard;
