import React, { useState } from 'react';
import { Doughnut, Bar } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement } from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

const FinanceDashboard = () => {
  const [timeRange, setTimeRange] = useState('Last 7 days');
  const [selectedFilters, setSelectedFilters] = useState(['Income', 'Expenses', 'Investments']);

  // Sample data
  const financialData = {
    balance: 12543.67,
    transactions: [
      { id: 1, category: 'Salary', amount: 3500, type: 'income', date: '2023-10-10' },
      { id: 2, category: 'Rent', amount: 1200, type: 'expense', date: '2023-10-11' },
      { id: 3, category: 'Investments', amount: 500, type: 'income', date: '2023-10-12' },
      { id: 4, category: 'Groceries', amount: 235.50, type: 'expense', date: '2023-10-12' },
    ],
    categories: {
      income: ['Salary', 'Freelance', 'Investments', 'Other'],
      expense: ['Rent', 'Groceries', 'Utilities', 'Entertainment']
    }
  };

  return (
    <div className="genomics-inspired-dashboard">
      <header className="dashboard-header">
        <h1>MY Finance</h1>
        <div className="time-selector">
          <select value={timeRange} onChange={(e) => setTimeRange(e.target.value)}>
            <option>Last 7 days</option>
            <option>Last 30 days</option>
            <option>Last quarter</option>
            <option>Custom range</option>
          </select>
        </div>
      </header>

      <div className="dashboard-grid">
        {/* Filters Panel */}
        <div className="panel filters-panel">
          <h2>Filters</h2>
          <div className="filter-group">
            <h3>Transaction Types</h3>
            {['Income', 'Expenses', 'Investments', 'Savings'].map(filter => (
              <label key={filter}>
                <input
                  type="checkbox"
                  checked={selectedFilters.includes(filter)}
                  onChange={() => setSelectedFilters(prev => 
                    prev.includes(filter) 
                      ? prev.filter(f => f !== filter) 
                      : [...prev, filter]
                  )}
                />
                {filter}
              </label>
            ))}
          </div>
          <div className="filter-dates">
            <p>Oct 10, 18:00</p>
            <p>Oct 17, 18:00</p>
          </div>
        </div>

        {/* Balance Overview */}
        <div className="panel balance-panel">
          <h2>Balance Overview</h2>
          <div className="balance-metric">
            <h3>Current Balance</h3>
            <p className="balance-amount">${financialData.balance.toLocaleString()}</p>
          </div>
          <div className="balance-details">
            <div>
              <h3>Income</h3>
              <p>$4,235.50</p>
            </div>
            <div>
              <h3>Expenses</h3>
              <p>$1,435.50</p>
            </div>
          </div>
        </div>

        {/* Transaction Categories */}
        <div className="panel categories-panel">
          <h2>Spending Categories</h2>
          <div className="doughnut-chart">
            <Doughnut
              data={{
                labels: financialData.categories.expense,
                datasets: [{
                  data: [1200, 235.5, 0, 0],
                  backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0']
                }]
              }}
            />
          </div>
          <div className="category-details">
            <p>Rent: $1200 (83.6%)</p>
            <p>Groceries: $235.50 (16.4%)</p>
          </div>
        </div>

        {/* Cash Flow */}
        <div className="panel cashflow-panel">
          <h2>Cash Flow</h2>
          <div className="bar-chart">
            <Bar
              data={{
                labels: ['Oct 10', 'Oct 11', 'Oct 12', 'Oct 13'],
                datasets: [
                  {
                    label: 'Income',
                    data: [3500, 0, 500, 0],
                    backgroundColor: '#4CC9F0'
                  },
                  {
                    label: 'Expenses',
                    data: [0, 1200, 235.5, 0],
                    backgroundColor: '#F72585'
                  }
                ]
              }}
              options={{
                responsive: true,
                scales: {
                  x: { stacked: true },
                  y: { stacked: true }
                }
              }}
            />
          </div>
          <div className="cashflow-details">
            <p>Net: $2,800</p>
            <p>Last 5 days</p>
          </div>
        </div>

        {/* Recent Transactions */}
        <div className="panel transactions-panel">
          <h2>Recent Transactions</h2>
          <div className="transactions-list">
            {financialData.transactions.map(tx => (
              <div key={tx.id} className="transaction-item">
                <div>
                  <h3>{tx.category}</h3>
                  <p>{tx.date}</p>
                </div>
                <p className={`amount ${tx.type}`}>
                  {tx.type === 'income' ? '+' : '-'}${tx.amount.toFixed(2)}
                </p>
              </div>
            ))}
          </div>
          <div className="transactions-summary">
            <p>Total: 4 transactions</p>
            <p>264 kcal</p>
          </div>
        </div>

        {/* Savings Goals */}
        <div className="panel savings-panel">
          <h2>Savings Progress</h2>
          <div className="savings-metric">
            <h3>Emergency Fund</h3>
            <p>$2,643.21 / $5,000</p>
            <div className="progress-bar">
              <div className="progress-fill" style={{ width: '52.86%' }}></div>
            </div>
          </div>
          <div className="savings-details">
            <p>52.86% complete</p>
            <p>176 kcal</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FinanceDashboard;
