import React, { useState, useEffect, useContext } from 'react';
import BalanceCard from '../components/dashboard/BalanceCard';
import RecentTransactions from '../components/dashboard/RecentTransactions';
import BudgetCard from '../components/dashboard/BudgetCard';
import SpendingChart from '../components/analytics/SpendingChart';
import MonthlyTrends from '../components/analytics/MonthlyTrends';
import AuthContext from '../context/AuthContext';

const Dashboard = () => {
  const { user } = useContext(AuthContext);
  const [transactions, setTransactions] = useState([]);
  const [balance, setBalance] = useState(0);
  const [showForm, setShowForm] = useState(false);
  const [newTransaction, setNewTransaction] = useState({
    type: 'expense',
    category: '',
    amount: '',
    description: ''
  });
  const [categories, setCategories] = useState([
    'Food', 'Transport', 'Housing', 'Entertainment', 'Utilities', 'Salary', 'Freelance', 'Other'
  ]);

  // Sample data for demonstration
  useEffect(() => {
    const sampleData = [
      { id: 1, type: 'expense', category: 'Food', amount: 45.00, description: 'Groceries', date: new Date() },
      { id: 2, type: 'expense', category: 'Transport', amount: 20.50, description: 'Bus fare', date: new Date() },
      { id: 3, type: 'income', category: 'Salary', amount: 2500.00, description: 'Monthly salary', date: new Date() },
    ];
    setTransactions(sampleData);
    updateBalance(sampleData);
  }, []);

  const updateBalance = (transactions) => {
    const calculatedBalance = transactions.reduce((acc, transaction) => {
      return transaction.type === 'income' 
        ? acc + transaction.amount 
        : acc - transaction.amount;
    }, 0);
    setBalance(calculatedBalance);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTransaction(prev => ({
      ...prev,
      [name]: name === 'amount' ? parseFloat(value) || 0 : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const transaction = {
      id: Date.now(),
      ...newTransaction,
      date: new Date()
    };
    
    const updatedTransactions = [...transactions, transaction];
    setTransactions(updatedTransactions);
    updateBalance(updatedTransactions);
    
    // Add new category if it doesn't exist
    if (!categories.includes(newTransaction.category)) {
      setCategories(prev => [...prev, newTransaction.category]);
    }
    
    // Reset form
    setNewTransaction({
      type: 'expense',
      category: '',
      amount: '',
      description: ''
    });
    setShowForm(false);
  };

  const deleteTransaction = (id) => {
    const updatedTransactions = transactions.filter(t => t.id !== id);
    setTransactions(updatedTransactions);
    updateBalance(updatedTransactions);
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>Welcome to Finance Prototype</h1>
        <p className="dashboard-subtitle">Add sample transactions to see visualizations update in real-time</p>
      </div>
      
      <div className="dashboard-controls">
        <button 
          className="add-transaction-btn"
          onClick={() => setShowForm(!showForm)}
        >
          {showForm ? 'Cancel' : '+ Add Sample Transaction'}
        </button>
        
        {showForm && (
          <div className="transaction-form-container">
            <form onSubmit={handleSubmit} className="transaction-form">
              <div className="form-group">
                <label>Type:</label>
                <select 
                  name="type" 
                  value={newTransaction.type}
                  onChange={handleInputChange}
                  required
                >
                  <option value="expense">Expense</option>
                  <option value="income">Income</option>
                </select>
              </div>
              
              <div className="form-group">
                <label>Category:</label>
                <input
                  type="text"
                  name="category"
                  value={newTransaction.category}
                  onChange={handleInputChange}
                  list="categories"
                  required
                />
                <datalist id="categories">
                  {categories.map(cat => (
                    <option key={cat} value={cat} />
                  ))}
                </datalist>
              </div>
              
              <div className="form-group">
                <label>Amount:</label>
                <input
                  type="number"
                  name="amount"
                  value={newTransaction.amount}
                  onChange={handleInputChange}
                  min="0"
                  step="0.01"
                  required
                />
              </div>
              
              <div className="form-group">
                <label>Description:</label>
                <input
                  type="text"
                  name="description"
                  value={newTransaction.description}
                  onChange={handleInputChange}
                />
              </div>
              
              <button type="submit" className="submit-btn">
                Add Transaction
              </button>
            </form>
          </div>
        )}
      </div>
      
      <div className="dashboard-grid">
        <BalanceCard balance={balance} />
        <BudgetCard transactions={transactions} />
        
        <RecentTransactions 
          transactions={transactions} 
          onDelete={deleteTransaction} 
        />
        
        <SpendingChart transactions={transactions} />
        <MonthlyTrends transactions={transactions} />
      </div>
    </div>
  );
};

export default Dashboard;
